from mymusic import PrimeDirdef test_primedir():    pd = PrimeDir()    pds = pd.get_prime_dirs()    assert isinstance(pds, list), f"PrimeDirs [{pds}] is not a list"    n_pds = 29  # Alphas + ["Num", "Xtra", "The"]    assert len(pds) == n_pds, f"Found [{len(pds)}] prime dirs. Expected {n_pds}"        pairs = []    pairs.append(["The Beatles", "The"])    pairs.append(["10,000 Maniacs", "Num"])    pairs.append([":wumpscut:", "Xtra"])    for artist_name, prime_dir in pairs:        retval = pd.get_prime_dir(artist_name)        assert retval == prime_dir, f"Expected pd({artist_name}) -> [{prime_dir}], but found [{retval}]"